interface Array<T> {
    /**
     * Access string by relative indexing.
     * @param index index to access.
     */
    item(index: number): T | undefined;
}


interface ReadonlyArray<T> {
    /**
     * Access string by relative indexing.
     * @param index index to access.
     */
    item(index: number): T | undefined;
}

